	private static final double eps = 1E-2;

	@Test
	public void testSqrtLasso() {
		Array2DRowRealMatrix x = new Array2DRowRealMatrix(xData, false);
		RealVector y = new ArrayRealVector(yData, false);
		double lambda = Math.sqrt(Math.log(x.getColumnDimension()) / x.getRowDimension());
		SqrtLasso lasso = new SqrtLasso(x);
		RealVector p = lasso.minimize(y, lambda);
		assertEquals(coeff.length, p.getDimension());
		for (int i = 0; i < coeff.length; i++)
			assertEquals(coeff[i], p.getEntry(i), eps);
	}

